package find_the_difference

import (
	"github.com/stretchr/testify/assert"
	"leet-code/src/core/domains"
	"testing"
)

/*
You are given two strings s and t.
String t is generated by random shuffling string s and then add one more letter at a random position.
Return the letter that was added to t.

Example 1:
Input: s = "abcd", t = "abcde"
Output: "e"
Explanation: 'e' is the letter that was added.

Example 2:
Input: s = "", t = "y"
Output: "y"


Constraints:

0 <= s.length <= 1000
t.length == s.length + 1
s and t consist of lowercase English letters.
*/

func findTheDifference(s string, t string) byte {
	var sum uint
	for i := 0; i < len(t); i++ {
		sum += uint(t[i])
	}

	for i := 0; i < len(s); i++ {
		sum -= uint(s[i])
	}

	return byte(sum)
}

func findTheDifference2(s string, t string) byte {
	var count int
	for _, val := range s + t {
		count ^= int(val)
	}
	return byte(count)
}

type Input struct {
	s string
	t string
}

var testcase = []domains.Testcase{
	{
		In: Input{
			s: "",
			t: "",
		},
		Out: byte('t'),
	},
	{
		In: Input{
			s: "abcd",
			t: "abcde",
		},
		Out: byte('e'),
	},
	{
		In: Input{
			s: "",
			t: "y",
		},
		Out: byte('y'),
	},
}

func TestFindTheDifference(t *testing.T) {
	for _, tt := range testcase {
		input := tt.In.(Input)
		output := tt.Out.(byte)
		assert.Equal(t, output, findTheDifference(input.s, input.t))
		assert.Equal(t, output, findTheDifference2(input.s, input.t))
	}
}
